import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { MdCheck, MdKeyboardArrowLeft } from 'react-icons/md';
import { Form, Input } from '@rocketseat/unform';
import Select from 'react-select';
import * as Yup from 'yup';
import { useDispatch } from 'react-redux';
import { toast } from 'react-toastify';

import { Container, EditOrder, FormOrder } from './styles';
import HeaderPages from '../../components/HeaderPages';
import history from '../../services/history';
import IconButton from '../../components/IconButton';
import ButtonBack from '../../components/ButtonBack';
import api from '../../services/api';
import * as OrdersActions from '../../store/modules/orders/actions';

const schema = Yup.object().shape({
  product: Yup.string().required('O nome do produto é obrigatório'),
  recipient_id: Yup.string().required('O destinatário é obrigatório'),
  delivaryman_id: Yup.string().required('O entregador é obrigatório'),
});

export default function EdicaoEncomendas({ match }) {
  const dispatch = useDispatch();

  const { id } = match.params;
  const [order, setOrders] = useState('');
  const [recipient_id, setRecipients] = useState([]);
  const [delivaryman_id, setDeliveries] = useState([]);
  const [product, setProducts] = useState([]);

  useEffect(() => {
    async function loadOrder() {
      if (id) {
        const response = await api.get(`orders/${id}`);

        const currentRecipient = {
          value: response.data.recipient.id,
          label: response.data.recipient.name,
        };

        const currentDelivery = {
          value: response.data.delivery.id,
          label: response.data.delivery.name,
        };

        response.data.currentRecipient = currentRecipient;
        response.data.currentDelivery = currentDelivery;

        setOrders(response.data);
      }
    }

    async function loadRecipients() {
      const response = await api.get('/recipient');

      const data = response.data.map(recipient_id => ({
        value: recipient_id.id,
        label: recipient_id.name,
      }));

      setRecipients(data);
    }

    async function loadDeliveries() {
      const response = await api.get('/deliveries');

      const dataDelivery = response.data.map(delivaryman_id => ({
        value: delivaryman_id.id,
        label: delivaryman_id.name,
      }));

      setDeliveries(dataDelivery);
    }

    loadOrder();
    loadRecipients();
    loadDeliveries();
  }, [id]);

  async function handleSubmit(data) {
    data.id = id;
    dispatch(OrdersActions.updateRequest(data));
  }

  const handleRecipientChange = e => {
    console.tron.log(e);
    recipient_id = e;
    console.tron.log(recipient_id);
    setRecipients(recipient_id);
  };

  return (
    <Container>
      <EditOrder>
        <Form initialData={order} schema={schema} onSubmit={handleSubmit}>
          <HeaderPages title="Edição de Encomendas">
            <ButtonBack
              Icon={MdKeyboardArrowLeft}
              title="VOLTAR"
              type="button"
              action={history.goBack}
            />
            <IconButton
              Icon={MdCheck}
              title="SALVAR"
              type="submit"
              action={() => handleSubmit(order)}
            />
          </HeaderPages>
          <FormOrder>
            <div className="divSelect" id="recipient_id">
              <label>Destinatário</label>
              <Select
                className="rec"
                name="recipient_id"
                placeholder={
                  order ? order.recipient.name : 'Selecione um entregador... '
                }
                defaultValue={order.recipient_id}
                options={recipient_id}
                onChange={handleRecipientChange}
              />
            </div>
            <div className="divSelect" id="delivaryman_id">
              <label>Entregadores</label>
              <Select
                className="del"
                name="delivaryman_id"
                placeholder={
                  order ? order.delivery.name : 'Selecione um entregador... '
                }
                defaultValue={order.delivaryman_id}
                options={delivaryman_id}
              />
            </div>
            <div className="divInp" id="product">
              <label>Nome do produto</label>
              <Input name="product" placeholder="Nome do produto" />
            </div>
          </FormOrder>
        </Form>
      </EditOrder>
    </Container>
  );
}

EdicaoEncomendas.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }).isRequired,
  }).isRequired,
};
