import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { MdCheck, MdKeyboardArrowLeft } from 'react-icons/md';
import { Form, Input } from '@rocketseat/unform';
import * as Yup from 'yup';
import { useDispatch } from 'react-redux';

import { Container, EditDeliveryman, FormDeliveryman } from './styles';
import HeaderPages from '../../../components/HeaderPages';
import history from '../../../services/history';
import IconButton from '../../../components/IconButton';
import ButtonBack from '../../../components/ButtonBack';
import api from '../../../services/api';
import * as RecipientActions from '../../../store/modules/recipient/actions';
import AvatarInput from '../AvatarInput';

const schema = Yup.object().shape({
  name: Yup.string().required('O nome do destinatário é obrigatório'),
  street: Yup.string().required('O nome da rua é obrigatório'),
  number: Yup.string().required('O número é obrigatório'),
  city: Yup.string().required('O nome da cidade é obrigatório'),
  state: Yup.string().required('O estado é obrigatório'),
  zip: Yup.number().required('O zip é obrigatório'),
});

export default function EdicaoEntregador({ match }) {
  const dispatch = useDispatch();

  const { id } = match.params;
  const [deliveryman, setDeliveryman] = useState('');
  const [, setName] = useState([]);
  const [, setEmail] = useState([]);

  async function loadDeliveryman() {
    if (id) {
      const response = await api.get(`deliveries/${id}`);
      setDeliveryman(response.data);
    }
  }

  useEffect(() => {
    loadDeliveryman(); // eslint-disable-next-line
  }, [id]);

  const handleNameChange = e => {
    setName(e.target.value);
  };

  const handleEmailChange = e => {
    setEmail(e.target.value);
  };

  async function handleSubmit({
    name,
    street,
    number,
    complement,
    city,
    state,
    zip,
  }) {
    dispatch(
      RecipientActions.updateRequest({
        id,
        name,
        street,
        number,
        complement,
        city,
        state,
        zip,
      })
    );
  }

  return (
    <Container>
      <EditDeliveryman>
        <Form initialData={deliveryman} schema={schema} onSubmit={handleSubmit}>
          <HeaderPages title="Edição de entregadores">
            <ButtonBack
              Icon={MdKeyboardArrowLeft}
              title="VOLTAR"
              type="button"
              action={history.goBack}
            />
            <IconButton
              Icon={MdCheck}
              title="SALVAR"
              type="submit"
              onClick={handleSubmit}
            />
          </HeaderPages>
          <FormDeliveryman>
            <AvatarInput
              name="avatar_id"
              src={deliveryman.avatar_id ? deliveryman.avatar.url : null}
            />
            <div className="divName" id="name">
              <p>Nome</p>
              <Input
                name="name"
                placeholder="Nome do entregador"
                onChange={handleNameChange}
              />
            </div>
            <div className="divEmail" id="email">
              <p>Email</p>
              <Input
                name="email"
                placeholder="Email do entregador"
                onChange={handleEmailChange}
              />
            </div>
          </FormDeliveryman>
        </Form>
      </EditDeliveryman>
    </Container>
  );
}

EdicaoEntregador.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }).isRequired,
  }).isRequired,
};
