import styled from 'styled-components';

export const Container = styled.div`
  height: 100%;
  background: #f5f5f5;
  opacity: 1;
  display: flex;
  justify-content: center;
  padding: 50px 120px;
`;

export const EditOrder = styled.div`
  width: 100%;
  max-width: 900px;
`;

export const FormOrder = styled.div`
  top: 150px;
  left: 270px;
  width: 900px;
  height: 224px;
  background: #ffffff 0% 0% no-repeat padding-box;
  border-radius: 4px;
  opacity: 1;

  div {
    display: flex;
    align-items: center;
    padding-bottom: 6px;

    label {
      margin-top: 20px;
      margin-left: 40px;
      justify-content: space-between;
      width: 77px;
      height: 19px;
      text-align: left;
      letter-spacing: 0;
      font-size: 14px;
      font-weight: bold;
      color: #444444;
      opacity: 1;
    }

    select {
      top: 204px;
      left: 300px;
      width: 405px;
      height: 45px;
      background: #ffffff 0% 0% no-repeat padding-box;
      border: 1px solid #dddddd;
      border-radius: 4px;
      opacity: 1;
    }
  }

  .labels {
    display: flex;
    align-items: center;
    padding-bottom: 6px;
    justify-content: space-between;
  }
`;
