export function updateRequest(order) {
  return {
    type: '@order/UPDATE_REQUEST',
    payload: {
      order,
    },
  };
}

export function updateSuccess(order) {
  return {
    type: '@order/UPDATE_SUCCESS',
    payload: {
      order,
    },
  };
}

export function addRequest(product, delivaryman_id, recipient_id) {
  return {
    type: '@order/ADD_REQUEST',
    payload: {
      product,
      delivaryman_id,
      recipient_id,
    },
  };
}

export function deleteRequest(id) {
  return {
    type: '@order/DELETE_REQUEST',
    payload: { id },
  };
}

export function deleteSuccess(id) {
  return {
    type: '@order/DELETE_SUCCESS',
    payload: { id },
  };
}

export function cancelRequest(id) {
  return {
    type: '@order/CANCEL_REQUEST',
    payload: { id },
  };
}

export function cancelSuccess(id) {
  return {
    type: '@order/CANCEL_SUCCESS',
    payload: { id },
  };
}
