import React, { useEffect } from 'react';
import {
  MdRemoveRedEye,
  MdEdit,
  MdDelete,
  MdFiberManualRecord,
} from 'react-icons/md';
import PropTypes from 'prop-types';
import Popup from 'reactjs-popup';
import { parseISO, format } from 'date-fns';

import {
  Container,
  Entregue,
  Retirada,
  Pendente,
  Cancelada,
  Button,
} from './styles';
import ButtonMore from '../../../components/ButtonMore';
import history from '../../../services/history';
import VisualizacaoEncomendas from '../VisualizacaoEncomendas';

export default function EncomendasTabela({ data }) {
  function formatDate(data) {
    return data.map(order => ({
      ...order,
      start_dateFormated: order.start_date
        ? format(parseISO(order.start_date), 'dd/MM/yyyy')
        : null,
      end_dateFormated: order.end_date
        ? format(parseISO(order.end_date), 'dd/MM/yyyy')
        : null,
      canceled_dateFormated: order.canceled_at
        ? format(parseISO(order.canceled_at), 'dd/MM/yyyy')
        : null,
    }));
  }

  useEffect(() => {
    formatDate();
  }, []);

  return (
    <Container>
      <small>#{data.id}</small>
      <small>{data.product}</small>
      <small>{data.recipient.name}</small>
      <small>{data.delivery.name}</small>
      <small>{data.recipient.city}</small>
      <small>{data.recipient.state}</small>
      <small>
        {data.end_dateFormated ? (
          <Entregue>
            <MdFiberManualRecord size={15} color="#2CA42B" />
            <strong>ENTREGUE</strong>
          </Entregue>
        ) : data.start_dateFormated ? (
          <Retirada>
            <MdFiberManualRecord size={15} color="#4D85EE" />
            <strong>RETIRADA</strong>
          </Retirada>
        ) : data.canceled_dateFormated ? (
          <Cancelada>
            <MdFiberManualRecord size={15} color="#de3b3b" />
            <strong>CANCELADA</strong>
          </Cancelada>
        ) : (
          <Pendente>
            <MdFiberManualRecord size={15} color="#c1bc35" />
            <strong>PENDENTE</strong>
          </Pendente>
        )}
      </small>

      <ButtonMore>
        <Button>
          <div>
            <Popup
              trigger={
                <button className="button">
                  {' '}
                  <MdRemoveRedEye color="#8E5BE8" size={10} />
                  <span>Visualizar</span>{' '}
                </button>
              }
              modal
            >
              {close => (
                <div className="modal">
                  <a className="close" onClick={close}>
                    &times;
                  </a>
                  <div className="header">Informações da encomenda</div>
                  <p className="street">
                    {data.recipient.street}, {data.recipient.number}
                  </p>
                  <p className="city">
                    {data.recipient.city} - {data.recipient.state}
                  </p>
                  <p className="zip">{data.recipient.zip}</p>
                  <div className="headerDate">Datas</div>
                  <label className="retirada">Retirada:</label>
                  <p className="dateRetirada">{data.start_date}</p>
                  <label className="entrega">Entrega:</label>
                  <p className="dateEntrega">{data.end_date}</p>
                </div>
              )}
            </Popup>
          </div>
          <div>
            <button
              onClick={() => history.push(`/encomenda/${data.id}`)}
              type="button"
            >
              <MdEdit color="#4D85EE" size={10} />
              <span>Editar</span>
            </button>
          </div>
          <div>
            <button onClick={() => history.push('/')} type="button">
              <MdDelete color="#DE3B3B" size={10} />
              <span>Excluir</span>
            </button>
          </div>
        </Button>
      </ButtonMore>
    </Container>
  );
}

EncomendasTabela.propTypes = {
  data: PropTypes.shape({
    id: PropTypes.number,
    product: PropTypes.string,
    recipient: PropTypes.shape({
      name: PropTypes.string,
      city: PropTypes.string,
      state: PropTypes.string,
    }),
    status: PropTypes.string,
  }).isRequired,
};
