export function updateRequest(recipients) {
  return {
    type: '@recipient/UPDATE_REQUEST',
    payload: {
      recipients,
    },
  };
}

export function updateSuccess(recipients) {
  return {
    type: '@recipient/UPDATE_SUCCESS',
    payload: {
      recipients,
    },
  };
}
