import styled from 'styled-components';

export const Container = styled.div`
  height: 100%;
  background: #f5f5f5;
  opacity: 1;
  display: flex;
  justify-content: center;
  padding: 50px 120px;
`;

export const EditRecipient = styled.div`
  width: 100%;
  max-width: 900px;
`;

export const FormRecipient = styled.div`
  top: 150px;
  left: 270px;
  width: 930px;
  height: 224px;
  background: #ffffff 0% 0% no-repeat padding-box;
  border-radius: 4px;
  opacity: 1;

  .divName {
    p {
      margin-top: 20px;
      margin-left: 20px;
      width: 37px;
      height: 19px;
      text-align: left;
      letter-spacing: 0;
      color: #444444;
      opacity: 1;
      font-size: 14px;
      font-weight: bold;
    }

    input {
      padding-top: 10px;
    }
  }
`;
