export function updateRequest(order) {
  return {
    type: '@order/UPDATE_REQUEST',
    payload: {
      order,
    },
  };
}

export function updateSuccess(order) {
  return {
    type: '@order/UPDATE_SUCCESS',
    payload: {
      order,
    },
  };
}

export function addRequest({ product, delivaryman_id, recipient_id }) {
  return {
    type: '@order/ADD_REQUEST',
    payload: {
      product,
      delivaryman_id,
      recipient_id,
    },
  };
}

export function addSuccess({ product, delivaryman_id, recipient_id }) {
  return {
    type: '@order/ADD_SUCCESS',
    payload: {
      product,
      delivaryman_id,
      recipient_id,
    },
  };
}
