import { all, takeLatest, call, put } from 'redux-saga/effects';
import { toast } from 'react-toastify';

import { updateSuccess } from './actions';
import api from '../../../services/api';
import history from '../../../services/history';

export function* update({ payload }) {
  try {
    const { id, recipientId, delivarymanId, product } = payload.order;

    const order = { id, recipientId, delivarymanId, product };

    const recipient_id = updated.recipientId.value;
    const delivaryman_id = updated.delivarymanId.value;

    order = { id, recipient_id, delivaryman_id, product };

    console.tron.log('OII', recipient_id);
    console.tron.log(order);

    const response = yield call(api.put, `orders/${id}`, {
      order,
    });

    console.tron.log('OII2', response.data);

    yield put(updateSuccess(response.data));
    toast.success('Encomenda atualizada com sucesso!');

    history.push('/encomendas');
  } catch (error) {
    toast.error(
      'Falha na atualização da encomenda, verifique os campos novamente.'
    );
  }
}

export default all([takeLatest('@order/UPDATE_REQUEST', update)]);
