import React, { useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import { MdCheck, MdKeyboardArrowLeft } from 'react-icons/md';

import { Container, EditOrder, FormOrder } from './styles';
import HeaderPages from '../../components/HeaderPages';
import history from '../../services/history';
import IconButton from '../../components/IconButton';
import ButtonBack from '../../components/ButtonBack';
import SelectAsync from '../../components/SelectAsync';
import api from '../../services/api';

export default function EdicaoEncomendas({ match }) {
  const { id } = match.params;
  const formRef = useRef(null);

  console.tron.log(id);

  useEffect(() => {
    async function loadInitialData(deliveryId) {
      if (id) {
        const response = await api.get(`/orders/${id}`);

        formRef.current.setData(response.data);
        formRef.current.setFieldValue('recipient_id', {
          value: response.data.recipient.id,
          label: response.data.recipient.name,
        });
        formRef.current.setFieldValue('delivery_id', {
          value: response.data.delivery.id,
          label: response.data.delivery.name,
        });
      }
    }
    loadInitialData(id);
  }, [id]);

  async function loadRecipients(inputValue, callback) {
    const response = await api.get('recipient', {
      params: {
        q: inputValue,
      },
    });

    const data = response.data.map(recipient => ({
      value: recipient.id,
      label: recipient.name,
    }));

    callback(data);
  }

  async function loadDeliveries(inputValue, callback) {
    const response = await api.get('deliveries', {
      params: {
        q: inputValue,
      },
    });

    const data = response.data.map(deliveries => ({
      value: deliveries.id,
      label: deliveries.name,
    }));

    callback(data);
  }

  return (
    <Container>
      <EditOrder>
        <HeaderPages title="Edição de Encomendas">
          <ButtonBack
            Icon={MdKeyboardArrowLeft}
            title="VOLTAR"
            type="button"
            action={history.goBack}
          />
          <IconButton
            Icon={MdCheck}
            title="SALVAR"
            type="button"
            action={() => history.push('/encomendas/')}
          />
        </HeaderPages>
        <FormOrder>
          <SelectAsync
            type="text"
            label="Destinatário"
            name="recipient_id"
            placeholder="Entregadores"
            noOptionsMessage={() => 'Nenhum destinatário encontrado'}
            loadOptions={loadRecipients}
          />
          <SelectAsync
            type="text"
            label="Entregador"
            name="delivery_id"
            placeholder="Entregadores"
            noOptionsMessage={() => 'Nenhum entregador encontrado'}
            loadOptions={loadDeliveries}
          />
        </FormOrder>
      </EditOrder>
    </Container>
  );
}

EdicaoEncomendas.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }).isRequired,
  }).isRequired,
};
