import React, { useEffect, useRef } from 'react';
import { toast } from 'react-toastify';

import PropTypes from 'prop-types';
import * as Yup from 'yup';

import IconButton from '../../components/IconButton';
import AsyncSelect from '../../components/AsyncSelect';
import HeaderPage from '../../components/HeaderPages';
import api from '../../services/api';
import history from '../../services/history';

import { Container, Content, UnForm } from './styles';

export default function Destinatario({ match }) {
  const { id } = match.params;
  const formRef = useRef(null);

  useEffect(() => {
    async function loadInitialData(deliveryId) {
      if (id) {
        const response = await api.get(`/orders/${deliveryId}`);

        formRef.current.setData(response.data);
        formRef.current.setFieldValue('recipient_id', {
          value: response.data.recipient.id,
          label: response.data.recipient.name,
        });
        formRef.current.setFieldValue('delivaryman_id', {
          value: response.data.delivery.id,
          label: response.data.delivery.name,
        });
      }
    }
    loadInitialData(id);
  }, [id]);

  const customStylesSelectInput = {
    control: provided => ({
      ...provided,
      height: 45,
    }),
  };

  async function loadRecipientOptions(inputValue, callback) {
    const response = await api.get('/recipients', {
      params: {
        q: inputValue,
      },
    });

    const data = response.data.map(recipient => ({
      value: recipient.id,
      label: recipient.name,
    }));

    callback(data);
  }

  async function loadDeliverymenOptrios(inputValue, callback) {
    const response = await api.get('/deliveries', {
      params: {
        q: inputValue,
      },
    });

    const data = response.data.map(deliveryman => ({
      value: deliveryman.id,
      label: deliveryman.name,
    }));

    callback(data);
  }

  async function handleSubmit(data, { reset }) {
    formRef.current.setErrors({});
    try {
      const schema = Yup.object().shape({
        product: Yup.string().required('O nome do produto é obrigatório'),
        recipient_id: Yup.string().required('O destinatário é obrigatório'),
        delivaryman_id: Yup.string().required('O entregador é obrigatório'),
      });

      await schema.validate(data, {
        abortEarly: false,
      });

      if (id) {
        await api.put(`/orders/${id}`, {
          product: data.product,
          recipient_id: data.recipient_id,
          delivaryman_id: data.delivery.id,
        });
        history.push('/encomendas');
        toast.success('Encomenda editada com sucesso!');
      } else {
        await api.post('/orders', {
          product: data.product,
          recipient_id: data.recipient_id,
          delivaryman_id: data.delivery.id,
        });
        toast.success('Encomenda criada com sucesso!');
      }

      reset();
    } catch (err) {
      if (err instanceof Yup.ValidationError) {
        const errorMessages = {};

        err.inner.forEach(error => {
          errorMessages[error.path] = error.message;
        });

        formRef.current.setErrors(errorMessages);
      }
    }
  }

  return (
    <Container>
      <Content>
        <HeaderPage title="Cadastro de encomendas">
          <IconButton action={() => formRef.current.submitForm()} />
        </HeaderPage>

        <UnForm ref={formRef} onSubmit={handleSubmit}>
          <section>
            <AsyncSelect
              type="text"
              label="Destinatário"
              name="recipient_id"
              placeholder="Destinatários"
              noOptionsMessage={() => 'Nenhum destinatário encontrado'}
              loadOptions={loadRecipientOptions}
              styles={customStylesSelectInput}
            />
            <AsyncSelect
              type="text"
              label="Entregador"
              name="delivaryman_id"
              placeholder="Entregadores"
              noOptionsMessage={() => 'Nenhum entregador encontrado'}
              loadOptions={loadDeliverymenOptrios}
              styles={customStylesSelectInput}
            />
          </section>
        </UnForm>
      </Content>
    </Container>
  );
}

Destinatario.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }).isRequired,
  }).isRequired,
};
