import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { MdCheck, MdKeyboardArrowLeft } from 'react-icons/md';
import { Form, Input } from '@rocketseat/unform';
import Select from 'react-select';
import * as Yup from 'yup';
import { useDispatch } from 'react-redux';

import { Container, EditOrder, FormOrder } from './styles';
import HeaderPages from '../../components/HeaderPages';
import history from '../../services/history';
import IconButton from '../../components/IconButton';
import ButtonBack from '../../components/ButtonBack';
import api from '../../services/api';
import { updateRequest } from '../../store/modules/orders/actions';

const schema = Yup.object().shape({
  product: Yup.string().required('O nome do produto é obrigatório'),
  recipient_id: Yup.string().required('O destinatário é obrigatório'),
  deliveryman_id: Yup.string().required('O entregador é obrigatório'),
});

export default function EdicaoEncomendas({ match }) {
  const { id } = match.params;
  const [order, setOrders] = useState('');
  const [recipients, setRecipients] = useState([]);
  const [deliveries, setDeliveries] = useState([]);
  const dispatch = useDispatch();

  useEffect(() => {
    async function loadOrder() {
      if (id) {
        const response = await api.get(`orders/${id}`);

        const currentRecipient = {
          value: response.data.recipient.id,
          label: response.data.recipient.name,
        };

        const currentDeliveryman = {
          value: response.data.delivery.id,
          label: response.data.delivery.name,
        };

        response.data.currentRecipient = currentRecipient;
        response.data.currentDeliveryman = currentDeliveryman;

        setOrders(response.data);
      }
    }

    async function loadRecipients() {
      const response = await api.get('/recipient');

      const data = response.data.map(recipients => ({
        value: recipients.id,
        label: recipients.name,
      }));

      setRecipients(data);
    }

    async function loadDeliveries() {
      const response = await api.get('/deliveries');

      const dataDelivery = response.data.map(deliveries => ({
        value: deliveries.id,
        label: deliveries.name,
      }));

      setDeliveries(dataDelivery);
    }

    loadOrder();
    loadRecipients();
    loadDeliveries();
  }, [id]);

  async function handleSubmit({ data, id }) {
    if (id) {
      data.id = data;
      dispatch(updateRequest(data));
    }
  }

  return (
    <Container>
      <EditOrder>
        <HeaderPages title="Edição de Encomendas">
          <ButtonBack
            Icon={MdKeyboardArrowLeft}
            title="VOLTAR"
            type="button"
            action={history.goBack}
          />
          <IconButton
            Icon={MdCheck}
            title="SALVAR"
            type="submit"
            action={handleSubmit}
          />
        </HeaderPages>
        <FormOrder>
          <Form initialData={order} schema={schema} onSubmit={handleSubmit}>
            <div className="divSelect">
              <label>Destinatário</label>
              <Select
                className="rec"
                name="recipient_id"
                placeholder={
                  order ? order.recipient.name : 'Selecione um destinatário... '
                }
                defaultValue={order.recipient_id}
                options={recipients}
              />
            </div>
            <div className="divSelect">
              <label>Entregadores</label>
              <Select
                className="del"
                name="deliveryman_id"
                placeholder={
                  order ? order.delivery.name : 'Selecione um entregador... '
                }
                defaultValue={order.deliveryman_id}
                options={deliveries}
              />
            </div>
            <div className="divInp">
              <label>Nome do produto</label>
              <Input name="product" type="text" placeholder={order.name} />
            </div>
          </Form>
        </FormOrder>
      </EditOrder>
    </Container>
  );
}

EdicaoEncomendas.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }).isRequired,
  }).isRequired,
};
