import styled from 'styled-components';

export const Container = styled.div`
  top: 0px;
  left: 0px;
  width: 100%;
  height: 100%;
  background: #f5f5f5 0% 0% no-repeat padding-box;
  opacity: 1;

  header {
    margin-left: 120px;
    padding-top: 50px;
    width: 281px;
    height: 32px;

    h2 {
      text-align: right;
      letter-spacing: 0;
      color: #444444;
      opacity: 1;
    }

    button {
      background: #ffffff 0% 0% no-repeat padding-box;
      border: 1px solid #dddddd;
      border-radius: 4px;
      opacity: 1;
      margin-top: 34px;
      margin-left: 35px;
      width: 237px;
      height: 36px;
      display: inline-block;
      margin-right: 10px;

      input {
        border: 0;
        margin: 0 10px;
        display: inline-block;

        &::placeholder {
          color: #999;
        }
      }
    }
  }
`;
