import React, { useState, useEffect } from 'react';
import {
  MdAdd,
  MdRemoveRedEye,
  MdEdit,
  MdDelete,
  MdFiberManualRecord,
} from 'react-icons/md';
import { parseISO, format } from 'date-fns';

import {
  Container,
  OrderContent,
  Table,
  OrderItem,
  Button,
  Retirada,
  Cancelada,
  Pendente,
  Entregue,
} from './styles';
import SearchInput from '../../components/Search';
import ButtonAdd from '../../components/ButtonAdd';
import HeaderList from '../../components/HeaderList';
import api from '../../services/api';
import history from '../../services/history';
import ButtonMore from '../../components/ButtonMore';

export default function Encomendas() {
  const [orders, setOrders] = useState([]);
  const [page, setPage] = useState(1);

  function formatDate(data) {
    return data.map(order => ({
      ...order,
      start_dateFormated: order.start_date
        ? format(parseISO(order.start_date), 'dd/MM/yyyy')
        : null,
      end_dateFormated: order.end_date
        ? format(parseISO(order.end_date), 'dd/MM/yyyy')
        : null,
      canceled_dateFormated: order.canceled_at
        ? format(parseISO(order.canceled_at), 'dd/MM/yyyy')
        : null,
    }));
  }

  async function loadOrders() {
    const response = await api.get('orders', {
      params: page,
    });

    const data = formatDate(response.data);
    setOrders(data);
  }

  useEffect(() => {
    loadOrders(); // eslint-disable-next-line
  }, [page]);

  async function handleSearch(e) {
    const response = await api.get('orders', {
      params: {
        q: e.target.value,
      },
    });

    const data = formatDate(response.data);
    setOrders(data);
  }

  return (
    <Container>
      <OrderContent>
        <HeaderList title="Gerenciando Encomendas">
          <SearchInput
            type="text"
            placeholder="Buscar por encomendas"
            onChange={handleSearch}
          />
          <ButtonAdd
            Icon={MdAdd}
            title="CADASTRAR"
            type="button"
            action={() => history.push('/encomendas/')}
          />
        </HeaderList>

        <Table>
          <section>
            <strong>ID</strong>
            <strong>Produto</strong>
            <strong>Destinatário</strong>
            <strong>Entregador</strong>
            <strong>Cidade</strong>
            <strong>Estado</strong>
            <strong>Status</strong>
            <strong>Ações</strong>
          </section>
        </Table>
        {orders.map(
          order => (
            console.tron.log(order),
            (
              <OrderItem key={order.id} data={order} updateOrder={loadOrders}>
                <small>#{order.id}</small>
                <small>{order.product}</small>
                <small>{order.recipient.name}</small>
                <small>{order.delivery.name}</small>
                <small>{order.recipient.city}</small>
                <small>{order.recipient.state}</small>
                <small>
                  {order.end_date ? (
                    <Entregue>
                      <MdFiberManualRecord size={15} color="#2CA42B" />
                      <strong>ENTREGUE</strong>
                    </Entregue>
                  ) : order.start_date ? (
                    <Retirada>
                      <MdFiberManualRecord size={15} color="#4D85EE" />
                      <strong>RETIRADA</strong>
                    </Retirada>
                  ) : order.canceled_at ? (
                    <Cancelada>
                      <MdFiberManualRecord size={15} color="#de3b3b" />
                      <strong>CANCELADA</strong>
                    </Cancelada>
                  ) : (
                    <Pendente>
                      <MdFiberManualRecord size={15} color="#c1bc35" />
                      <strong>PENDENTE</strong>
                    </Pendente>
                  )}
                </small>
                <ButtonMore>
                  <Button>
                    <div>
                      <button onClick={() => history.push('/')} type="button">
                        <MdRemoveRedEye color="#8E5BE8" size={10} />
                        <span>Visualizar</span>
                      </button>
                    </div>
                    <div>
                      <button
                        onClick={() => history.push(`/encomenda/${order.id}`)}
                        type="button"
                      >
                        <MdEdit color="#4D85EE" size={10} />
                        <span>Editar</span>
                      </button>
                    </div>
                    <div>
                      <button onClick={() => history.push('/')} type="button">
                        <MdDelete color="#DE3B3B" size={10} />
                        <span>Excluir</span>
                      </button>
                    </div>
                  </Button>
                </ButtonMore>
              </OrderItem>
            )
          )
        )}
      </OrderContent>
    </Container>
  );
}
