import { all, takeLatest, call, put } from 'redux-saga/effects';
import { toast } from 'react-toastify';

import { updateSuccess } from './actions';
import api from '../../../services/api';
import history from '../../../services/history';

export function* updateRecipient({ payload }) {
  try {
    const {
      id,
      name,
      street,
      number,
      complement,
      state,
      city,
      zip,
    } = payload.recipients;

    yield call(api.put, `recipient/${id}`, {
      name,
      street,
      number,
      complement,
      state,
      city,
      zip,
    });

    yield put(
      updateSuccess({ name, street, number, complement, state, city, zip })
    );

    toast.success('Destinatário atualizado com sucesso!');

    history.push('/destinatarios');
  } catch (error) {
    toast.error(
      'Falha na atualização de destinatário, verifique os campos novamente.'
    );
  }
}

export default all([takeLatest('@recipient/UPDATE_REQUEST', updateRecipient)]);
