import produce from 'immer';

const initialState = {
  product: null,
};

export default function auth(state = initialState, action) {
  return produce(state, draft => {
    switch (action.type) {
      case '@order/UPDATE_SUCCESS': {
        draft.product = action.payload.product;
        break;
      }
      case '@order/DELETE': {
        const orderIndex = draft.findIndex(order => order.id === action.id);

        console.tron.log(orderIndex, 'chegou aqui');
        if (orderIndex >= 0) {
          draft.splice(orderIndex, 1);
        }
        break;
      }
      default:
        return state;
    }
  });
}
