import React from 'react';
import { MdEdit, MdDelete } from 'react-icons/md';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';

import { Container, Button } from './styles';
import ButtonMore from '../../../components/ButtonMore';
import history from '../../../services/history';
import * as RecipientActions from '../../../store/modules/recipient/actions';

export default function ProblemasTabela({ data, updateProblem }) {
  const dispatch = useDispatch();

  function handleDelete() {
    var confirmDelete = window.confirm(
      `Você tem certeza que deseja deletar o destinatário ${data.name}?`
    );

    if (confirmDelete === true) {
      dispatch(RecipientActions.deleteRequest(data.id));
      updateProblem();
    } else {
      history.push('/destinatarios');
    }
  }

  return (
    <Container>
      <small>#{data.delivery_id}</small>
      <small>{data.description}</small>

      <ButtonMore>
        <Button>
          <div>
            <button onClick={handleDelete} type="button">
              <MdDelete color="#DE3B3B" size={10} />
              <span>Cancelar</span>
            </button>
          </div>
        </Button>
      </ButtonMore>
    </Container>
  );
}

ProblemasTabela.propTypes = {
  data: PropTypes.shape({
    id: PropTypes.number,
    delivery_id: PropTypes.number,
    description: PropTypes.string,
  }).isRequired,
  updateProblem: PropTypes.func.isRequired,
};
