import styled from 'styled-components';
import { Form } from '@unform/web';

export const Container = styled.div`
  height: 100%;
  background: #f5f5f5;
  opacity: 1;
  display: flex;
  justify-content: center;
  padding: 50px 120px;
`;

export const EditOrder = styled.div`
  width: 100%;
  max-width: 900px;
`;

export const FormOrder = styled(Form)`
  top: 150px;
  left: 270px;
  width: 900px;
  height: 224px;
  background: #ffffff 0% 0% no-repeat padding-box;
  border-radius: 4px;
  opacity: 1;
`;

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
`;

export const Label = styled.label`
  color: #444;
  font-weight: bold;
  margin-top: 20px;
  margin-left: 40px;
  width: 77px;
  height: 19px;
  text-align: left;
  letter-spacing: 0;
  opacity: 1;
  font-size: 16px;

  :last-child {
    margin-left: 400px;
    margin-right: 50px;
  }
`;
