import styled from 'styled-components';

export const Container = styled.div`
  top: 0px;
  left: 0px;
  width: 1440px;
  height: 900px;
  background: #f5f5f5 0% 0% no-repeat padding-box;
  opacity: 1;
  display: block;

  h2 {
    width: 281px;
    height: 32px;
    text-align: right;
    letter-spacing: 0;
    color: #444444;
    opacity: 1;
    margin-left: 120px;
    padding-top: 50px;
  }

  .search {
    margin-top: 64px;
    margin-left: 155px;
    width: 237px;
    background: #ffffff 0% 0% no-repeat padding-box;
    border: 1px solid #dddddd;
    border-radius: 4px;
    opacity: 1;

    input {
      background: none;
      border: none;
      margin: 0 10px;

      &::placeholder {
        text-align: left;
        letter-spacing: 0;
        color: #999;
        opacity: 1;
      }
    }
  }
`;
